const cityNameInput = $("#cityName");
const cityImgSelect = $("#cityimg");
const addBtn = $("#btn");
const DivToDrawTo = $("#drawTo");
const options = document.querySelectorAll("option");
const domImages = {
  Holon:
    "https://www.malamteam.com/wp-content/uploads/2018/07/%D7%97%D7%95%D7%9C%D7%95%D7%9F.png",
  Ashdod:
    "data:image/jpeg;base64,",
  TelAviv:
    "data:image/jpeg;base64,",
  Eilat:
    "data:image/jpeg;base64,",
  BatYam:
    "https://www.bigcenters.co.il/uploadImages/systemFiles/%D7%A9%D7%A2%D7%A8_%D7%92%D7%9C%D7%A8%D7%99%D7%94386742132.jpg",
  Rehovot:
    "data:image/jpeg;base64,",
};

addBtn.submit(function (e) {
  e.preventDefault();
});

addBtn.on("click", () => {
  if (cityNameInput.val().toLowerCase() !== cityImgSelect.val().toLowerCase()) {
    return alert("The City Name and The City Img Selected does not match ");
  }
  const mainDiv = $("<div></div>")
    .addClass("card")
    .css({ display: "inline-block" });
  let cityImgUrl = domImages[cityImgSelect.val()];
  const cityImg = $(`<img src="${cityImgUrl}" alt="City Image">`)
    .addClass("card-img-top")
    .css({ width: "450px", height: "400px" });
  const cardbody = $("<div></div>").addClass("card-body");
  const cityName = $(`<h5>${cityNameInput.val()}</h5>`).addClass("card-title");
  const p = $(`<p>${cityNameInput.val()} is The Best City Ever !!!</p>`);
  const likeBtn = $(`<input type="button" id ="Like"  >`)
    .addClass("btn btn-primary")
    .val("like");
  let numOfLikes = 0;
  const LikesCounter = $(`<h6>${numOfLikes}</h6>`);
  const deleteBtn = $(`<input type="button" id ="delete"  >`)
    .addClass("btn btn-danger")
    .val("X")
    .css({ display: "block" });

  mainDiv.append(deleteBtn);
  mainDiv.append(cityImg);
  mainDiv.append(cardbody);
  cardbody.append(cityName);
  cardbody.append(p);
  cardbody.append(likeBtn);
  cardbody.append(LikesCounter);

  DivToDrawTo.append(mainDiv);

  likeBtn.on("click", () => {
    setTimeout(() => {
      function plusOne() {
        numOfLikes++;
        LikesCounter.text(numOfLikes + "");
      }
      plusOne();
    }, 2000);
  });
  deleteBtn.click(() => {
    mainDiv.fadeOut("slow" , ()=>{
      this.remove()
    });
  });
});
